import React, { useEffect, useState } from 'react';
import booklibrarydetails from './booklibrarydetails';
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const [editPostId, setEditPostId] = useState(null);
  const [editFormData, setEditFormData] = useState({
    title: '',
    genre: '',
    author: '',
    publication_year:'',
  });

  useEffect(() => {
    booklibrarydetails.getBooks()
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      booklibrarydetails.deleteBooks(id)
        .then(() => {
          setPosts(posts.filter(post => post.id !== id));
        })
        .catch(error => {
          console.error('Error deleting post:', error);
        });
    }
  };

  const handleEditClick = (post) => {
    setEditPostId(post.id);
    setEditFormData({
      title: books.title,
      author: books.author,
      genre: books.genre,
      publication_year: books.publication_year,
      
    });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleEditSave = (id) => {
    booklibrarydetails.updateBooks(id, editFormData)
      .then(() => {
        setPosts(posts.map(post => post.id === id ? { ...post, ...editFormData } : post));
        setEditPostId(null);
      })
      .catch(error => {
        console.error('Error updating post:', error);
      });
  };

  return (
    <div>
      <h1>Book Library</h1>
      <div>
        <button onClick={() => navigate("/")}>Add Book</button>
      </div>
      <div><h2>MY BOOKS</h2></div>

      {books.length === 0 ? (
        <p>No books found.</p>
      ) : (
        books.map(post => (
          <div  key={post.id}>
            {editPostId === post.id ? (
              <>
                <input
                  type="text"
                  name="title"
                  value={editFormData.title}
                  onChange={handleEditChange}
                  placeholder="Title"
                />

                <input
                  type="text"
                  name="author"
                  value={editFormData.author}
                  onChange={handleEditChange}
                  placeholder="Author"
                />

                <input
                  type="text"
                  name="genre"
                  value={editFormData.genre}
                  onChange={handleEditChange}
                  placeholder="Genre"
                />

                <input
                  type="text"
                  name="publication_year"
                  value={editFormData.publication_year}
                  onChange={handleEditChange}
                  placeholder="Publication Year"
                />
                <div className="post-actions">
                  <button onClick={() => handleEditSave(post.id)} >Save</button>
                  <button onClick={() => setEditPostId(null)} >Cancel</button>
                </div>
              </>
            ) : (
              <>
                <h2>{books.title}</h2>
                <p>{books.author}</p>
                <p>{books.genre}</p>
                <p>{books.publication_year}</p>
                <p>
                  By: {books.author} | Date: {new Date(books.created_at).toISOString().split('T')[0]}
                </p>
                <div>
                  <button  onClick={() => handleEditClick(post)}>Edit</button>
                  <button  onClick={() => handleDelete(post.id)}>Delete</button>
                </div>
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default Dashboard;
