import './homepage.css';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const Home = () => {
  const navigate = useNavigate();
  const [searchTitle, setSearchTitle] = useState('');

  const handleSearch = () => {
    if (searchTitle.trim()) {
      navigate(`/book/${encodeURIComponent(searchTitle.trim())}`);
    }
  };

  return (
    <div className="homepage-container">
      <div className="homepage-header">
        <h1>Book Library</h1>
        <button onClick={() => navigate("/dash")}>My Books</button>
      </div>

      {/* Search Section */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search book by title"
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <div className="homepage-genre-buttons">
        <div className="homepage-genre-button" onClick={() => navigate("/sci-fi")}>
          <img src="Images/sct-fi.png" alt="sci-fi" />
        </div>
        {/* Other genre buttons... */}
      </div>
    </div>
  );
};

export default Home;
