import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import booklibrarydetails from './booklibrarydetails';

const AddBook = () => {
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    publication_year: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const validate = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.author.trim()) {
      newErrors.author = 'Author name is required';
    }

    if (!formData.genre.trim()) {
      newErrors.genre = 'Genre is required';
    }

    if (!formData.publication_year.trim()) {
      newErrors.publication_year = 'Publication year is required';
    } else if (!/^\d{4}$/.test(formData.publication_year)) {
      newErrors.publication_year = 'Enter a valid 4-digit year';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      try {
        await booklibrarydetails.saveBookDetails(formData);
        alert('Book added successfully');
        navigate('/'); 
      } catch (err) {
        alert('Failed to add book: ' + err.message);
      }
    }
  };

  return (
    <div>
      <h1>Add a New Book</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title</label><br />
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Enter book title"
          />
          {errors.title && <p>{errors.title}</p>}
        </div>

        <div>
          <label htmlFor="author">Author</label><br />
          <input
            type="text"
            id="author"
            name="author"
            value={formData.author}
            onChange={handleChange}
            placeholder="Enter author name"
          />
          {errors.author && <p>{errors.author}</p>}
        </div>

        <div>
          <label htmlFor="genre">Genre</label><br />
          <input
            type="text"
            id="genre"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
            placeholder="Enter genre"
          />
          {errors.genre && <p>{errors.genre}</p>}
        </div>

        <div>
          <label htmlFor="publication_year">Publication Year</label><br />
          <input
            type="text"
            id="publication_year"
            name="publication_year"
            value={formData.publication_year}
            onChange={handleChange}
            placeholder="Enter publication year"
          />
          {errors.publication_year && <p>{errors.publication_year}</p>}
        </div>

        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
