import React, { useEffect, useState } from "react";
import booklibrarydetails from "./booklibrarydetails";
import { useNavigate } from "react-router-dom";
import "./stylesbook.css";

const Romance = () => {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    booklibrarydetails.getBooks("romance")
      .then(response => {
        
        const romanceBooks = response.data.filter(book =>
          book.genre.toLowerCase() === "romance"
        );
        setBooks(romanceBooks);
      })
      .catch(error => {
        console.error('Error fetching romance books:', error);
      });
  }, []);

  return (
    <div className="scifi-container">
      <h1 className="scifi-title">Romance Books</h1>
      <button onClick={() => navigate("/")} className="scifi-back-button">
        Back
      </button>
      {books.length === 0 ? (
        <p>No romance books found.</p>
      ) : (
        books.map(book => (
          <div className="book-card" key={book.id}>
            <h2 className="book-title">{book.title}</h2>
            <p className="book-author">Author: {book.author}</p>
            <p className="book-genre">Genre: {book.genre}</p>
            <p className="book-year">Published: {book.publication_year}</p>
            <p className="book-meta">
              Added on: {new Date(book.created_at).toISOString().split('T')[0]}
            </p>
          </div>
        ))
      )}
    </div>
  );
};

export default Romance;
